sect3.programlisting:
let decorate_row ~sep row = "|" ^ String.concat ~sep row ^ "|"
let render_row widths row = decorate_row ~sep:"|" (List.map2_exn row widths ~f:(fun s w -> " " ^ pad s w ^ " "))
let render_separator widths = decorate_row ~sep:"+" (List.map widths ~f:(fun width -> String.make (width + 2) '-'))

sect2.programlisting:
let length l =
  let rec tail_recursive_length len = function
    | [] -> len
    | _ :: t -> tail_recursive_length (len + 1) t
  in tail_recursive_length 0 l;;

let rec map f = function
 | [] -> []
 | h :: t -> f h :: map f t;;

let rev l =
  let rec tail_recursive_rev result = function
   | [] -> result
   | h :: t -> tail_recursive_rev (h :: result) t
  in tail_recursive_rev [] l;;

let rev_map l ~f =
  let rec rmap accu = function
   | [] -> accu
   | h :: t -> rmap (f h :: accu) l
  in rmap [] l;;

let map l ~f = rev (rev_map l ~f);;

let map_slow l ~f = rev (rev_map l ~f);;
let rec count_map ~f l ctr = match l with | [] -> [] | [x1] -> let f1 = f x1 in [f1] | [x1; x2] -> let f1 = f x1 in let f2 = f x2 in [f1; f2] | [x1; x2; x3] -> let f1 = f x1 in let f2 = f x2 in let f3 = f x3 in [f1; f2; f3] | [x1; x2; x3; x4] -> let f1 = f x1 in let f2 = f x2 in let f3 = f x3 in let f4 = f x4 in [f1; f2; f3; f4] | x1 :: x2 :: x3 :: x4 :: x5 :: tl -> let f1 = f x1 in let f2 = f x2 in let f3 = f x3 in let f4 = f x4 in let f5 = f x5 in f1 :: f2 :: f3 :: f4 :: f5 :: (if ctr > 1000 then map_slow ~f tl else count_map ~f tl (ctr + 1));;
let map l ~f = count_map ~f l 0;;

