class ['a] node x = object val mutable value : 'a = x val mutable next_node : 'a node option = None
method get = value method set x = value <- x
method next = next_node method set_next node = next_node <- node end;;

class ['a] slist = object val mutable first : ('a) node option = None val mutable last : ('a) node option = None
method is_empty = first = None
method insert x = let new_node = Some (new node x) in match last with Some last_node -> last_node#set_next new_node; last <- new_node | None -> first <- new_node; last <- new_node end;;

// Java-style iterator, specified as an interface.
interface <T> iterator {
  T Get();
  boolean HasValue();
  void Next();
};

// Abstract class definition in C++.
template<typename T>
class Iterator {
 public:
  virtual ~Iterator() {}
  virtual T get() const = 0;
  virtual bool has_value() const = 0;
  virtual void next() = 0;
};

