sect3.programlisting:
type t = [ | `Add of t * t | `Sub of t * t | `Mul of t * t | `Num of int ]
let rec algebra (x:t) = match x with | `Add (x,y) -> (algebra x) + (algebra y) | `Sub (x,y) -> (algebra x) - (algebra y) | `Mul (x,y) -> (algebra x) * (algebra y) | `Num x -> x
let _ = algebra ( `Add ( (`Num 0), (`Sub ( (`Num 1), (`Mul ( (`Nu 3),(`Num 2) )) )) ))

type s = { foo: int; bar: unit }
type t = { foo: int }

let f x =
  x.bar;
  x.foo

type s = { foo: int; bar: unit }
type t = { foo: int }

let f (x:s) =
  x.bar;
  x.foo
